// Custom validation rule for validating description
var errorMsg = "";
$.formUtils.addValidator({
	name : 'validateDescription',
	validatorFunction : function(value, $el, config, language, $form) {
		
		if(value == ""){
			$el.attr("data-validation-error-msg", "Please describe your project details.");
			return false;
		}
		
		var taCheckTypes = {
			at : {
				regEx : /@|\[at\]|\(at\)|\{at\}|\-at\-|\+at\+|\[dot\]|\(dot\)|\{dot\}|\-dot\-|\+dot\+/,
				message : "Terms of Service reminder: Providing email, Skype, or phone number is only allowed if it is needed as part of the service. Otherwise, all communication must go through Nlance."
			},
			connect : {
				regEx : /email|skype.me|skype|messanger|contact.me.at|send.to.my/i,
				message : "Terms of Service reminder: Providing email, Skype, or phone number is only allowed if it is needed as part of the service. Otherwise, all communication must go through Nlance."
			},
			payment : {
				regEx : /paypal|alertpay|money|pay/i,
				message : "Reminder: Never accept or ask for direct payments. Doing so may get your account restricted."
			},
			customOffer : {
				regEx : /dollar|\$|cost|pay|order|gig/i,
				message : "Reminder: Never accept or ask for direct payments. Doing so may get your account restricted."
			}
		};
		var flag = false;
		$.each(["at", "connect", "payment", "customOffer"], function(i, val) {
			if (value.match(taCheckTypes[val].regEx)) {
				flag = false;
				errorMsg = taCheckTypes[val].message;
				$el.attr("data-validation-error-msg", errorMsg);
				//console.log(errorMsg);
				return false;
			} else {
				flag = true;
			}
		});

		//console.log(errorMsg);
		return flag;
	},
	errorMessage : errorMsg,
	errorMessageKey : 'badDescription'
});

